import { useEffect } from 'react';
import { useRendererLayout } from '../context/rendererLayoutContext';
import '../styles/node.scss';

const Node = ({data, index}) => {

    const {offsetX, offsetY, scale} = useRendererLayout();

    useEffect(() => {
        nodeStyle = {
            width: `${width * scale}px`,
            height: `${height * scale}px`,
            left: `${(x + offsetX) * scale}px`,
            top: `${(y + offsetY) * scale}px`,
            fontSize: `${fontSize * scale}px`,
            padding: `${10 * scale}px`,
        }
    }, [scale]);

    const {id = 1, name = '', fields = [], methods = [], width, height, x = 0, y = 0, fontSize = 12} = data;

    let nodeStyle = {
        width: `${width * scale}px`,
        height: `${height * scale}px`,
        left: `${(x + offsetX) * scale}px`,
        top: `${(y + offsetY) * scale}px`,
        fontSize: `${fontSize * scale}px`,
        padding: `${10 * scale}px`,
    }

    console.log(scale);

    return (
        <div className='node' id={id} style={nodeStyle}>
            <h1>{data.name}</h1>
            {(methods.length > 0 || fields.length > 0) && (<div className='divider'></div>)} 
            {fields.length > 0 && fields.map((field,i) => <p key={i}>{field.toString()}</p>)}
            {methods.length > 0 && fields.length > 0 && (<div className='divider'></div>)} 
            {methods.length > 0 && methods.map((method,i) => <p key={i}>{method.toString()}</p>)}
        </div>
    )
}

export default Node;
