import { createContext, useRef } from 'react';

export const RendererContext = createContext();

export const RendererProvider = ({ children }) => {
  const offsetXRef = useRef(0);
  const offsetYRef = useRef(0);
  const scaleRef = useRef(1);

  const updateOffsetX = newOffsetX => offsetXRef.current = newOffsetX;
  const updateOffsetY = newOffsetY => offsetYRef.current = newOffsetY;
  const updateScale = newScale => scaleRef.current = newScale;

  return (
    <RendererContext.Provider value = {{
      offsetX: offsetXRef.current,
      offsetY: offsetYRef.current,
      scale: scaleRef.current,
      updateOffsetX,
      updateOffsetY,
      updateScale
    }}>{children}</RendererContext.Provider>
  )
}
