import React, { createContext, useContext, useState, useEffect, useCallback } from 'react';

const LayoutContext = createContext(null);

export function RendererLayoutProvider({ children }) {
  const [state, setState] = useState(() => {
    const saved = localStorage.getItem('rendererLayoutState');
    if (saved) {
      return JSON.parse(saved);
    }
    return {
      offsetX: 0,
      offsetY: 0,
      scale: 1,
    };
  });

  useEffect(() => {
    localStorage.setItem('rendererLayoutState', JSON.stringify(state));
  }, [state]);

  const setScaleAndOffset = useCallback((newScale, newOffsetX, newOffsetY) => {
    console.log('Context receiving update:', { newScale, newOffsetX, newOffsetY });
    setState(prev => {
      const updated = {
        ...prev,
        scale: newScale,
        offsetX: newOffsetX,
        offsetY: newOffsetY
      };
      console.log('State updated to:', updated);
      return updated;
    });
  }, []);

  // Individual setters now use setScaleAndOffset
  const setScale = useCallback((value) => {
    setScaleAndOffset(value, state.offsetX, state.offsetY);
  }, [state.offsetX, state.offsetY, setScaleAndOffset]);

  const setOffsetX = useCallback((value) => {
    setScaleAndOffset(state.scale, value, state.offsetY);
  }, [state.scale, state.offsetY, setScaleAndOffset]);

  const setOffsetY = useCallback((value) => {
    setScaleAndOffset(state.scale, state.offsetX, value);
  }, [state.scale, state.offsetX, setScaleAndOffset]);

  return (
    <LayoutContext.Provider value={{
      ...state,
      setOffsetX,
      setOffsetY,
      setScale,
      setScaleAndOffset,
    }}>
      {children}
    </LayoutContext.Provider>
  );
}

export function useRendererLayout() {
  const context = useContext(LayoutContext);
  if (context === null) {
    throw new Error('useRendererLayout must be used within a RendererLayoutProvider');
  }
  return context;
}
