import { useAppLayout } from "../context/appLayoutContext";
import '../styles/renderer.scss';
import { useRef } from 'react'

import Viewport from './viewport';
import Node from './node';

import { RendererLayoutProvider } from '../context/rendererLayoutContext';

const layoutNodes = () => {
    const adjList = new Map();

    for(const node of rootNode.classList){
        adjList.set(node, []);
    }

    for(const edge of rootNode.relationshipList){
        adjList.get(edge.source).push(edge.destination);
    }

    const levels = computeLevels(adjList);
}

const computeLevels = (adjList) => {
    const levels = new Map();
    const visited = new Set();

    const visitDFS = (nodeID, currentLevel) => {
        if(visited.has(nodeID)) return;

        visited.add(nodeID);
        levels.set(nodeID, Math.max(levels.get(nodeID) || 0, currentLevel));

        for(const nei of adjList.get(nodeID)){
            visitDFS(nei, currentLevel + 1);
        }
    }

    for (const nodeID in adjList){
        visitDFS(nodeID, 0);
    }

    return levels;
}


function Renderer() {
    return (
        <RendererLayoutProvider>
            <RendererContent />
        </RendererLayoutProvider>
    )
}

function RendererContent() {
    const { rendererHeight, rendererWidth, rootNode } = useAppLayout();

    let renderer = useRef(null);

    const rendererStyle = {
        width: `${rendererWidth}vw`,
        height: `${rendererHeight}vh`,
    }

    // const overlayViewBox = `0 0 ${rendererWidth * window.innerWidth} ${rendererHeight * window.innerHeight}`;

    return (
        <div ref={renderer} id='renderer' style={rendererStyle}>
            {/* <svg xmlns="http://www.w3.org/2000/svg" preserveAspectRatio='xMidYMid meet' viewBox={overlayViewBox} id='relationship-overlay'></svg> */}
            <Viewport></Viewport>

            <div id='node-container'>
                
            </div>
        </div>
    )
}

export default Renderer;
