import { useAppLayout } from "../context/appLayoutContext";
import '../styles/renderer.scss';
import { useRef } from 'react'

import Viewport from './viewport';
import Node from './node';

import { RendererLayoutProvider } from '../context/rendererLayoutContext';

function Renderer() {
    return (
        <RendererLayoutProvider>
            <RendererContent />
        </RendererLayoutProvider>
    )
}

function RendererContent() {
    const { rendererHeight, rendererWidth, rootNode } = useAppLayout();

    let renderer = useRef(null);
    let testNode = useRef(null);

    let testNodeData = {id: -1};

    // Calculate necessary width to accomadate node text
    const calculateNodeBounds = () => {

        if(!testNode.current) return;
        if(!rootNode) return;

        for(const node of rootNode.nodes || []){
            testNodeData = node;
            let nodeId = node.id;
            testNodeData.id = -1;

            console.log(testNode.current.getBoundingClientRect().width);

            node.width = testNode.current.getBoundingClientRect().width;
            node.height = testNode.current.getBoundingClientRect().height;
        }
    }

    calculateNodeBounds();


    const rendererStyle = {
        width: `${rendererWidth}vw`,
        height: `${rendererHeight}vh`,
    }

    // const overlayViewBox = `0 0 ${rendererWidth * window.innerWidth} ${rendererHeight * window.innerHeight}`;

    return (
        <div ref={renderer} id='renderer' style={rendererStyle}>
            {/* <svg xmlns="http://www.w3.org/2000/svg" preserveAspectRatio='xMidYMid meet' viewBox={overlayViewBox} id='relationship-overlay'></svg> */}
            <Viewport></Viewport>

            <div id='node-container'>
                <Node ref={testNode} data={testNodeData} index={-1}></Node>
                {rootNode && rootNode.nodes.map((nodeData, i) => <Node key={i} data={nodeData} index={nodeData.id} ></Node>)}
            </div>
        </div>
    )
}

export default Renderer;
