import { useAppLayout } from "../context/appLayoutContext";
import '../styles/renderer.scss';
import { useRef } from 'react'

import Viewport from './viewport';
import Node from './node';

import { RendererLayoutProvider } from '../context/rendererLayoutContext';

function Renderer() {
    return (
        <RendererLayoutProvider>
            <RendererContent />
        </RendererLayoutProvider>
    )
}

function RendererContent() {
    const { rendererHeight, rendererWidth, rootNode } = useAppLayout();

    let renderer = useRef(null);

    const adjList = new Map();

    const HORIZONTAL_MARGIN = 50;
    const VERTICAL_MARGIN = 150;
    const CLUSTER_MARGIN = 200;    

    const nodeIdToNode = new Map();

    for(const node of rootNode.nodes) nodeIdToNode.set(node.id, node);        

    const calculateLayout = () => {
        // possibility of having no root node on initial load
        if(rootNode === null) return;

        // possibility of having no nodes to render
        if(rootNode.nodes === undefined) return;

        // possibility of having no edges to render
        if(rootNode.edges === undefined) return;

        // build adj list
        for(const node of rootNode.nodes) adjList.set(node.id, []);
        for(const edge of rootNode.edges) adjList.get(edge.sourceId).push(edge.targetId);

        // create level map for assigning vertical placement
        const levels = createLevels();

        console.log(levels);

        // assign positions to nodes
        assignPositions(levels);
    }

    const createLevels = () => {
        const visited = new Set();
        const levels = new Map();

        const visit = (nodeId, currentLevel) => {
            if(visited.has(nodeId)) return;
            visited.add(nodeId);

            levels.set(nodeId, Math.max(levels.get(nodeId) || 0, currentLevel));

            for(const neiId of adjList.get(nodeId) || []){
                visit(neiId, currentLevel + 1);
            }
        }
        
        for(const nodeId of adjList.keys()){
            visit(nodeId, 0);
        }

        return levels;
    }

    const createClusters = () => {
        const clusters = [];
        const visited = new Set();

        const dfs = (nodeId, cluster) => {
            if(visited.has(nodeId)) return;

            visited.add(nodeId);
            cluster.push(nodeId);

            for(const edge of rootNode.edges){
                if(edge.sourceId === nodeId && !visited.has(edge.targetId)){
                    dfs(edge.sourceId, cluster);
                }
                else if (edge.targetId === nodeId && !visited.has(edge.targetId)){
                    dfs(edge.targetId, cluster);
                }
            }
        }

        for(const node of rootNode.nodes){
            if(visited.has(node.id)) continue;

            const cluster = [];
            dfs(node.id, cluster);
            clusters.push(cluster);
        }

        return clusters;
    }

    const assignPositions = (levels, clusters) => {

        // group nodes by levels
        const levelToNodes = new Map();

        for(const node of rootNode.nodes){
            const level = levels.get(node.id);
            
            if(!levelToNodes.has(level)) levelToNodes.set(level, []);
            levelToNodes.get(level).push(node.id);
        }

        // assign positions level by level

        for(const [level, nodes] of levelToNodes){
            let y = level * VERTICAL_MARGIN;
            let xOffset = 0;
            for(const nodeId of nodes) nodeIdToNode.get(nodeId).y = y

            for(const cluster of clusters){
                for(const nodeId of cluster){
                    nodeIdToNode.get(nodeId).x = xOffset;
                    xOffset += HORIZONTAL_MARGIN;
                }
            }
        }
    }

    calculateLayout();

    const rendererStyle = {
        width: `${rendererWidth}vw`,
        height: `${rendererHeight}vh`,
    }

    // const overlayViewBox = `0 0 ${rendererWidth * window.innerWidth} ${rendererHeight * window.innerHeight}`;

    return (
        <div ref={renderer} id='renderer' style={rendererStyle}>
            {/* <svg xmlns="http://www.w3.org/2000/svg" preserveAspectRatio='xMidYMid meet' viewBox={overlayViewBox} id='relationship-overlay'></svg> */}
            <Viewport></Viewport>

            <div id='node-container'>
                {rootNode && rootNode.nodes.map((nodeData, i) => <Node key={i} data={nodeData} index={nodeData.id} ></Node>)}
            </div>
        </div>
    )
}

export default Renderer;
